import Phaser from 'phaser';

/**
 * Example: Loading and using TEXTURE ATLAS (Best of Both Worlds)
 * This uses a sprite sheet with JSON metadata that preserves frame names
 * Generated by tools like TexturePacker
 */
export default class BootSceneTextureAtlas extends Phaser.Scene {
    constructor() {
        super({ key: 'BootScene' });
    }

    preload() {
        const width = this.cameras.main.width;
        const height = this.cameras.main.height;
        
        // Create loading bar
        const progressBar = this.add.graphics();
        const progressBox = this.add.graphics();
        progressBox.fillStyle(0x222222, 0.8);
        progressBox.fillRect(width / 2 - 160, height / 2 - 25, 320, 50);
        
        this.load.on('progress', (value) => {
            progressBar.clear();
            progressBar.fillStyle(0xffffff, 1);
            progressBar.fillRect(width / 2 - 150, height / 2 - 15, 300 * value, 30);
        });
        
        this.load.on('complete', () => {
            progressBar.destroy();
            progressBox.destroy();
        });

        // === LOAD TEXTURE ATLAS (PNG + JSON) ===
        // The JSON preserves your original file names as frame names!
        this.load.atlas('player', 
            'assets/images/player-atlas.png',
            'assets/images/player-atlas.json'
        );
        
        this.load.atlas('tiles',
            'assets/images/tiles-atlas.png', 
            'assets/images/tiles-atlas.json'
        );
    }

    create() {
        // === CREATE ANIMATIONS FROM ATLAS ===
        // You can use the original file names!
        
        // Idle animation
        this.anims.create({
            key: 'player-idle',
            frames: [{ key: 'player', frame: 'idle.png' }],
            frameRate: 1
        });
        
        // Walk animation - Use frame names from original files
        this.anims.create({
            key: 'player-walk',
            frames: [
                { key: 'player', frame: 'walk-1.png' },
                { key: 'player', frame: 'walk-2.png' },
                { key: 'player', frame: 'walk-3.png' },
                { key: 'player', frame: 'walk-4.png' }
            ],
            frameRate: 10,
            repeat: -1
        });
        
        // Jump animation
        this.anims.create({
            key: 'player-jump',
            frames: [{ key: 'player', frame: 'jump.png' }],
            frameRate: 1
        });
        
        // Fall animation
        this.anims.create({
            key: 'player-fall',
            frames: [{ key: 'player', frame: 'fall.png' }],
            frameRate: 1
        });
        
        this.scene.start('GameScene');
    }
}
